import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
    this.setState({
      data: [],
    });
  }

  getData() {
    const { page } = this.state;
    this.setState({
      listData: [],
    });
    axios
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((res) => {
        console.log('check', res.data.data);
        this.setState({
          listData: res.data.data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }

  render() {
    const { currentPage, } = this.state;
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
        </table>
        <button
          href='#'
          onClick={() => this.PrePage()}
          disabled={currentPage === 1 ? 'disabled' : ''}
        >
          Pre
        </button>
        <button
          href='#'
          onClick={() => this.NextPage(maxPage)}
          disabled={currentPage === maxPage ? 'disabled' : ''}
        >
          Next
        </button>
      </div>
    );
  }
}
export default Pagination;
