import React from 'react';
class Counter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],
    };
  }
  handleDelete = (e, id) => {
    e.preventDefault();
    const post = this.state.post.filter((post) => c.id !== id);
    this.setState({
      post: post,
    });
  };
  render() {
    const { post, c } = this.state;
    return (
      <div>
        <h1>
          Hello you
          {post.map((post) => (
            <tr key={post.id}>
              <td>{post.id}</td>
              <td>{post.title}</td>
              <td>{post.title}</td>
              <td>
                <button
                  className='btn btn-danger'
                  onClick={(e) => this.handleDelete(e, post.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </h1>
      </div>
    );
  }
}

export default Counter;
