import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      data: [],
      totalCount: 0,
      page: 1,
    };
  }
  getData() {
    const { page } = this.state;
    axios
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((res) => {
        console.log('check', this.setState);
        this.setState({
          data: res.data.data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }

  render() {
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody></tbody>
        </table>
      </div>
    );
  }
}
export default Pagination;
