import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
  }

  getData = async () => {
    const { page, limit } = this.state;
    const url = `http://localhost/php_api/users/list.php/${page}&${limit}`;

    let result = await axios({
      method: 'get',
      url,
      total: '',
      data: [],
      headers: {
        Authorization: ``,
        'Content-Type': 'application/json',
      },
    });
    result = result.data;
    console.log('checked', result);
    this.setState({
      listData: result.data,
    });
  };

  loadMore = () => {
    this.getData(false);
  };

  gotoPage = (page) => {
    this.getData(true);
    this.setState({
      currentPage: page,
    });
  };

  nextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }
    this.setState({
      currentPage,
    });
    this.getData(true);
  };
  prePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage < 0) {
      return false;
    }
    this.setState({ currentPage });
    this.getData(true);
  };

  renderPage = (maxPage) => {
    const { currentPage } = this.state;

    let startPage = 0;
    let endPage = 0;
    let start = 0;
    let end = 0;

    if (currentPage + 2 >= maxPage) {
      end = maxPage;
    } else {
      end = currentPage + 2;
    }
    if (currentPage - 2 <= 1) {
      start = 1;
    } else {
      start = currentPage - 2;
    }

    startPage = start;
    endPage = end;
    const extraStart = start - (currentPage - 2);
    if (extraStart > 0) {
      end = end + extraStart;
    }
    const extraEnd = currentPage + 2 - endPage;
    if (extraEnd > 0) {
      start = start - extraEnd;
    }
    if (start <= 1) {
      start = 1;
    }
    if (end >= maxPage) {
      end = maxPage;
    }

    console.log('startPage', startPage);
    console.log('endPage', endPage);

    let html = [];
    for (let i = start; i <= end; i++) {
      html.push(
        <button
          key={i}
          className={`${currentPage === i ? 'active' : ''}`}
          onClick={() => this.gotoPage(i)}
        >
          {i}
        </button>
      );
    }

    return html;
  };

  render() {
    const { listData, total, limit } = this.state;
    console.log('total', total);
    console.log('limit', limit);
    const extraPage = total % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(total / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>

            {listData.map((post, i) => {
              return (
                <div className='content' key={i}>
                  {post.email}
                </div>
              );
            })}
          </thead>
          <button onClick={this.prePage()}
          
          
          
          
          
          
          
          
          >pre</button>
          {this.renderPage(maxPage)}

          <button onClick={this.nextPage(maxPage)}>next</button>
        </table>
      </div>
    );
  }
}
export default Pagination;
