import React from 'react';

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      toggle: false,
    };
  }
  handleShowHide = () => {
    this.setState({
      toggle: !this.state.toggle,
    });
  };

  render() {
    return (
      <div>
        {toggle ? (
          <h1>Trung</h1>
        ) : (
          <button onClick={() => this.handleShowHide()}>ok</button>
        )}
      </div>
    );
  }
}

export default Counter;
