import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
  }

  // getData() {
  //   const { page } = this.state;
  //   this.setState({
  //     listData: [],
  //   });
  //   axios.get(`http://localhost/php_api/users/pagination.php/${page}`)
  //     .then((res) => {
  //       console.log('check', res.data);
  //       this.setState({
  //         listData: res.data.data,
  //       });
  //     })
  //     .catch((e) => {
  //       console.error(e);
  //     });
  // }

  render() {
    const { listData } = this.state;
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>

            {listData.map((post, i) => {
              return (
                <div className='content' key={i}>
                  {post.email}
                </div>
              );
            })}
          </thead>
        </table>
      </div>
    );
  }
}
export default Pagination;
