import React from 'react';
import axios from 'axios';
import './search.css';

const queryString = require('query-string');

class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      totalCount: 0,
      currentPage: 1,
    };
  }

  componentDidMount = () => {
    this.loadData(true);

    const parsed = queryString.parse(this.props.location.search);
    const keyword = parsed.query || '';
    if (!keyword) {
      this.props.history.push('/');
    }
    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }
  };

  loadData = async () => {
    let res = await axios.get('http://localhost/php_api/users/pagination.php');
    console.log('check', this.setState);
    this.setState({
      posts: res.data.data,
    });
  };

  loadMore = () => {
    this.loadData(false);
  };

  gotoPage = (page) => {
    this.loadData(true);
    this.setState({
      currentPage: page,
    });
  };

  NextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }

    this.setState({ currentPage });
    this.loadData(true);
  };
  PrePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage === 0) {
      return false;
    }
    this.setState({ currentPage });
    this.loadData(true);
  };

  renderPage = (maxPage) => {
    const { currentPage } = this.state;

    let startPage = 0;
    let endPage = 0;
    let start = 0;
    let end = 0;

    if (currentPage + 2 >= maxPage) {
      end = maxPage;
    } else {
      end = currentPage + 2;
    }
    if (currentPage - 2 <= 1) {
      start = 1;
    } else {
      start = currentPage - 2;
    }

    startPage = start;
    endPage = end;
    const extraStart = start - (currentPage - 2);
    if (extraStart > 0) {
      end = end + extraStart;
    }
    const extraEnd = currentPage + 2 - endPage;
    if (extraEnd > 0) {
      start = start - extraEnd;
    }
    if (start <= 1) {
      start = 1;
    }
    if (end >= maxPage) {
      end = maxPage;
    }

    console.log('startPage', startPage);
    console.log('endPage', endPage);

    let html = [];
    for (let i = start; i <= end; i++) {
      html.push(
        <button
          key={i}
          className={`${currentPage === i ? 'active' : ''}`}
          onClick={() => this.gotoPage(i)}
        >
          {i}
        </button>
      );
    }

    return html;
  };

  render() {
    const { listData, numberOfPage, totalCount, limit, currentPage } =
      this.state;
    console.log('totalCount', totalCount);
    console.log('limit', limit);
    console.log('numberOfPage', numberOfPage);
    const extraPage = totalCount % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(totalCount / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div className='container'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
              <br />
              <img src={post.thumbnail} style={{ maxWidth: 200 }} />
            </div>
          );
        })}
        <br></br>
        <div className='pagination'>
          <button
            href='#'
            onClick={() => this.PrePage()}
            disabled={currentPage === 1 ? 'disabled' : ''}
          >
            Pre
          </button>
          {this.renderPage(maxPage)}
          <button
            href='#'
            onClick={() => this.NextPage(maxPage)}
            disabled={currentPage === maxPage ? 'disabled' : ''}
          >
            Next
          </button>
        </div>
        <div class='flex'></div>
      </div>
    );
  }
}

export default Pagination;
