import React from 'react';
import axios from 'axios';
const queryString = require('query-string');

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      listData: [],
      meta: {},
    };
    this.state.loadData();
  }

  componentDidMount = async () => {
    const parsed = queryString.parse(this.props.location.search);
    const keyword = parsed.query || '';
    if (!keyword) {
      this.props.history.push('/');
    }

    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }

    loadData = () => {
      const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

      let result = axios({
        method: 'post',
        url,
        data: {
          operationName: 'ListBlogPosts',
          query: `query ListBlogPosts {
            listBlogPosts(
              limit: 2
              sort: postDate_DESC
              after: ""
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
        },
        headers: {
          Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
          'Content-Type': 'application/json',
        },
      });

      result = result.data;
      const {
        data: {
          listBlogPosts: { data, meta },
        },
      } = result;

      console.log('meta', meta);
      this.setState({
        listData: data,
        meta,
      });
    };
  };

  render() {
    const { listData, meta } = this.state;

    return (
      <div className='container'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
              <br />
              <img src={post.thumbnail} style={{ maxWidth: 200 }} />
            </div>
          );
        })}
        <br></br>
        {meta.hasMoreItems && (
          <button onClick={() => this.loadMore()}>Load more</button>
        )}
      </div>
    );
  }
}

export default Search;
