import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
    this.setState({
      data: [],
    });
  }

  getData() {
    const { page } = this.state;
    this.setState({
      listData: [],
    });
    axios
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((res) => {
        console.log('check', res.data.data);
        this.setState({
          listData: res.data.data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }

  loadMore = () => {
    this.getData(false);
  };

  gotoPage = (page) => {
    this.loadData(true);
    this.setState({
      currentPage: page,
    });
  };

  NextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }

    this.setState({ currentPage });
    this.loadData(true);
  };

  PrePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage === 0) {
      return false;
    }
    this.setState({ currentPage });
    this.getData(true);
  };

  renderPage = (maxPage) => {
    const { currentPage } = this.state;

    let startPage = 0;
    let endPage = 0;
    let start = 0;
    let end = 0;

    if (currentPage + 2 >= maxPage) {
      end = maxPage;
    } else {
      end = currentPage + 2;
    }
    if (currentPage - 2 <= 1) {
      start = 1;
    } else {
      start = currentPage - 2;
    }

    startPage = start;
    endPage = end;
    const extraStart = start - (currentPage - 2);
    if (extraStart > 0) {
      end = end + extraStart;
    }
    const extraEnd = currentPage + 2 - endPage;
    if (extraEnd > 0) {
      start = start - extraEnd;
    }
    if (start <= 1) {
      start = 1;
    }
    if (end >= maxPage) {
      end = maxPage;
    }

    console.log('startPage', startPage);
    console.log('endPage', endPage);

    let html = [];
    for (let i = start; i <= end; i++) {
      html.push(
        <button
          key={i}
          className={`${currentPage === i ? 'active' : ''}`}
          onClick={() => this.gotoPage(i)}
        >
          {i}
        </button>
      );
    }

    return html;
  };

  render() {
    const { listData, total, limit, currentPage } = this.state;
    console.log('total', total);
    console.log('limit', limit);

    const extraPage = total % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(total / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              {listData.map((post, i) => {
                return (
                  <div className='content' key={i}>
                    {post.email}
                  </div>
                );
              })}
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
        </table>
        <button
          href='#'
          onClick={() => this.PrePage()}
          disabled={currentPage === 1 ? 'disabled' : ''}
        >
          Pre
        </button>
        {this.renderPage(maxPage)}
        <button
          href='#'
          onClick={() => this.NextPage(maxPage)}
          disabled={currentPage === maxPage ? 'disabled' : ''}
        >
          Next
        </button>
      </div>
    );
  }
}
export default Pagination;
