import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
  }

  // getData() {
  //   const { page } = this.state;
  //   this.setState({
  //     listData: [],
  //   });
  //   axios.get(`http://localhost/php_api/users/pagination.php/${page}`)
  //     .then((res) => {
  //       console.log('check', res.data);
  //       this.setState({
  //         listData: res.data.data,
  //       });
  //     })
  //     .catch((e) => {
  //       console.error(e);
  //     });
  // }

  getData = async (firstLoad) => {
    const {  limit } = this.state;

    const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

    let result = await axios({
      method: 'get',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: ${limit}
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                id
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    console.log('checked', result);
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    const { listData } = this.state;
    console.log('meta', meta);
    this.setState({
      listData: firstLoad ? data : listData.concat(data),
      metaInfo: meta,
      totalCount: meta.totalCount || 0,
    });
  };

  render() {
    const { listData } = this.state;
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>

            {listData.map((post, i) => {
              return (
                <div className='content' key={i}>
                  {post.email}
                </div>
              );
            })}
          </thead>
        </table>
      </div>
    );
  }
}
export default Pagination;
