import React from 'react';
import axios from 'axios';
import './search.css';

const queryString = require('query-string');

class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      totalCount: 0,
      currentPage: 1,
    };
  }

  componentDidMount = () => {
    this.loadData(true);

    const parsed = queryString.parse(this.props.location.search);
    const keyword = parsed.query || '';
    if (!keyword) {
      this.props.history.push('/');
    }
    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }
  };

  loadData = async () => {
    let res = await axios.get('http://localhost/php_api/users/pagination.php');
    console.log('check', this.setState);
    this.setState({
      posts: res.data.data,
    });
  };


  

 

  render() {
 

    return (
      <div className='container'>
       
        <br></br>
        <div className='pagination'>
          <button
            href='#'
            onClick={() => this.PrePage()}
            disabled={currentPage === 1 ? 'disabled' : ''}
          >
            Pre
          </button>
          {this.renderPage(maxPage)}
          <button
            href='#'
            onClick={() => this.NextPage(maxPage)}
            disabled={currentPage === maxPage ? 'disabled' : ''}
          >
            Next
          </button>
        </div>
        <div class='flex'></div>
      </div>
    );
  }
}

export default Pagination;
