import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
    this.setState({
      data: [],
    });
  }

  getData() {
    const { page } = this.state;
    this.setState({
      listData: [],
    });
    axios
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((res) => {
        console.log('check', res.data.data);
        this.setState({
          listData: res.data.data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }

  loadMore = () => {
    this.getData(false);
  };

  gotoPage = (page) => {
    this.getData(true);
    this.setState({
      currentPage: page,
    });
  };

  NextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }

    this.setState({ currentPage });
    this.loadData(true);
  };

  PrePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage === 0) {
      return false;
    }
    this.setState({ currentPage });
    this.getData(true);
  };

  

  render() {
    const { listData, total, limit, currentPage } = this.state;
    console.log('total', total);
    console.log('limit', limit);

    const extraPage = total % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(total / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              {listData.map((post, i) => {
                return (
                  <div className='content' key={i}>
                    {post.email}
                  </div>
                );
              })}
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
        </table>
        <button
          href='#'
          onClick={() => this.PrePage()}
          disabled={currentPage === 1 ? 'disabled' : ''}
        >
          Pre
        </button>
        {this.renderPage(maxPage)}
        <button
          href='#'
          onClick={() => this.NextPage(maxPage)}
          disabled={currentPage === maxPage ? 'disabled' : ''}
        >
          Next
        </button>
      </div>
    );
  }
}
export default Pagination;
