import React from 'react';
import axios from 'axios';
import './search.css';

const queryString = require('query-string');

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 2,
      listData: [],
      metaInfo: {
        hasMoreItems: false,
        cursor: '',
        meta: {},
      },
      totalCount: 0,
      currentPage: 1,
    };
  }

  componentDidMount = () => {
    this.loadData(true);

    const parsed = queryString.parse(this.props.location.search);
    const keyword = parsed.query || '';
    // if (!keyword) {
    //   this.props.history.push('/');
    // }
    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }
  };

  loadData = async (firstLoad) => {
    const { metaInfo, limit } = this.state;

    const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

    let result = await axios({
      method: 'post',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: ${limit}
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    console.log('checked', result);
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    const { listData } = this.state;
    console.log('meta', meta);
    this.setState({
      listData: firstLoad ? data : listData.concat(data),
      metaInfo: meta,
      totalCount: meta.totalCount || 0,
    });
  };

  loadMore = () => {
    this.loadData(false);
  };

  gotoPage = (page) => {
    this.loadData(true);
    this.setState({
      currentPage: page,
    });
  };

  NextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }

    this.setState({ currentPage });
    this.loadData(true);
  };
  PrePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage === 0) {
      return false;
    }
    this.setState({ currentPage });
    this.loadData(true);
  };

  renderPage = (maxPage) => {
    const { currentPage } = this.state;
    // const { endPage } = this.state;
    let startPage = 0;
    let endPage = 0;
    if (currentPage + 2 >= maxPage) {
      endPage = maxPage;
    } else {
      endPage=currentPage+2
    }

    let html = [];
    for (let i = 0; i < 5; i++) {
      html.push(
        <button
          key={i}
          className={`${currentPage === i + 1 ? 'active' : ''}`}
          onClick={() => this.gotoPage(i + 1)}
        >
          {i + 1}
        </button>
      );
    }

    return html;
  };

  render() {
    const { listData, numberOfPage, totalCount, limit, currentPage } =
      this.state;
    console.log('totalCount', totalCount);
    console.log('limit', limit);
    console.log('numberOfPage', numberOfPage);
    const extraPage = totalCount % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(totalCount / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div className='container'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
              <br />
              <img src={post.thumbnail} style={{ maxWidth: 200 }} />
            </div>
          );
        })}
        <br></br>
        <div className='pagination'>
          <button
            href='#'
            onClick={() => this.PrePage()}
            disabled={currentPage === 1 ? 'disabled' : ''}
          >
            Pre
          </button>
          {this.renderPage(maxPage)}
          <button
            href='#'
            onClick={() => this.NextPage(maxPage)}
            disabled={currentPage === maxPage ? 'disabled' : ''}
          >
            Next
          </button>
        </div>
        <div class='flex'></div>
      </div>
    );
  }
}

export default Search;
