import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }

  componentDidMount() {
    this.getData(true);
  }

  // getData() {
  //   const { page } = this.state;
  //   this.setState({
  //     listData: [],
  //   });
  //   axios.get(`http://localhost/php_api/users/pagination.php/${page}`)
  //     .then((res) => {
  //       console.log('check', res.data);
  //       this.setState({
  //         listData: res.data.data,
  //       });
  //     })
  //     .catch((e) => {
  //       console.error(e);
  //     });
  // }

  getData = async () => {
    const url = `http://localhost/php_api/users/list.php/${page}&${limit}`;

    let result = await axios({
      method: 'get',
      url,
      total: '',
      data: [],
      headers: {
        Authorization: ``,
        'Content-Type': 'application/json',
      },
    });
    result = result.data;
    console.log('checked', result);
    this.setState({
      listData: result.data,
    });
  };

  loadMore = () => {
    this.getData(false);
  };

  gotoPage = (page) => {
    this.getData(true);
    this.setState({
      currentPage: page,
    });
  };

  nextPage = () => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }
    this.setState({
      currentPage,
    });
    this.getData(true);
  };

  render() {
    const { listData, total, limit } = this.state;
    console.log('total', total);
    console.log('limit', limit);
    const extraPage = total % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(total / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>

            {listData.map((post, i) => {
              return (
                <div className='content' key={i}>
                  {post.email}
                </div>
              );
            })}
          </thead>

          {/* <button
            href='#'
            onClick={() => this.NextPage(maxPage)}
            disabled={currentPage === maxPage ? 'disabled' : ''}
          >
            Next
          </button> */}
        </table>
      </div>
    );
  }
}
export default Pagination;
