import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      data: [],
      totalCount: 0,
      page: 1,
    };
  }

  getData() {
    const { page } = this.state;
AC
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((json) => {
        console.log('check', json);
        this.setState({
          data: json,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }
  componentDidMount() {
    this.getData();
    this.setState({
      data: [],
    });
  }

  render() {
    const { data } = this.state;
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
          {data.map((post, i) => {
            return (
              <div className='content' key={i}>
                {post.email}
                <br />
              </div>
            );
          })}
        </table>
      </div>
    );
  }
}
export default Pagination;
