import { useState } from 'react';

const gifts = ['Iphone', 'Ram', 'RGB'];
function Gift() {
  const [gift, setGift] = useState();
  const radomGift = () => {
    const index = Math.floor(Math.random() * gifts.length);
    setGift(gifts[index]);
  };

  return (
    <div>
      {gift || 'no gift'}
      <button onClick={randomGift}>OK</button>
    </div>
  );
}

export default Gift;
