import React from 'react';
class Counter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],
    };
  }

  handleShow = () => {
    this.setState({ post: this.state.counters });
  };

  render() {
    const { counters } = this.state;
    return (
      <h1>
        Hello you
        {counters.map((counter) => {
          <div className='content' key={counters.id}>
            {counter.value}
          </div>;
        })}
      </h1>
    );
  }
}

export default Counter;
