import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      limit: 5,
      posts: [],
    };
  }
  loadData = async (firstLoad) => {
    const { limit } = this.state;

    const url = `http://localhost/php_api/users/pagination.php`;

    let result = await axios({
      method: 'post',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: ${limit}
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    console.log('checked', result);
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    const { listData } = this.state;
    console.log('meta', meta);
    this.setState({
      listData: firstLoad ? data : listData.concat(data),
      metaInfo: meta,
      totalCount: meta.totalCount || 0,
    });
  };

  render() {
    const { posts } = this.state;
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <tr key={post.id}>
                <td>{post.email}</td>
                <td>{post.pass}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
export default Pagination;
