import axios from 'axios';
import React from 'react';

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      listData: [],
      metaInfo: {
        hasMoreItems: false,
        cusor: '',
      },
    };
  }

  //
  async componentDidMount() {
    this.loadData();
    const token = localStorage.getItem('token') || '';
    console.log('check', token);
    if (!token) {
      this.props.history.push('/login');
    }
  }

  loadData = async () => {
    const { metaInfo } = this.state;

    const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

    let result = await axios({
      method: 'post',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: 2
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    const { listData } = this.state;
    this.setState({
      listData: !metaInfo.cursor ? data : [...[], ...listData].concat(data),
      metaInfo: meta,
    });
  };

  render() {
    const { listData } = this.state;
    return (
      <div className='container-fluid'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
            </div>
          );
        })}
      </div>
    );
  }
}

export default Home;
