import React from 'react';
class Counter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],
    };
  }
  handleDelete = (e, id) => {
    e.preventDefault();
    let post = this.state.post.filter((post) => post.id !== id);
    this.setState({
      post,
    });
  };
  render() {
    const { post } = this.state;
    return (
      <div>
        <h1>
          Hello you
          {post.map((post) => {
            return (
              <div className='content' key={post.id}>
                {post.value}
              </div>
            );
          })}
        </h1>
        <button onClick={() => this.handleDelete(e, post.id)}>Delete</button>
      </div>
    );
  }
}

export default Counter;
