import React from 'react';
import axios from 'axios';
import './Users.css';

class Users extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }
  //
  // componentDidMount() {
  //   const token = localStorage.getItem('token') || '';
  //   console.log('check', token);
  //   if (!token) {
  //     this.props.history.push('/login');
  //   }

  //   this.gotoPage(0);
  // }
  componentDidMount() {
    const token = localStorage.getItem('token') || '';

    console.log('check', token);
    if (!token) {
      this.props.history.push('/login');
    }
    this.gotoPage(0);
  }

  getData = async (page) => {
    const { limit } = this.state;
    const url = `http://localhost/php_api/users/list.php/?page=${page}&limit=${limit}`;

    let result = await axios({
      method: 'get',
      url,
      data: {
        email: '',
        id: '',
      },
      headers: {
        Authorization: ``,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    const { data } = result;

    console.log('checked', result);

    this.setState({
      listData: data,
      total: result.total,
    });
  };

  //

  gotoPage = (page) => {
    this.setState({
      currentPage: page,
    });
    this.getData(page);
  };

  //
  // nextPage = (maxPage) => {
  //   let { currentPage } = this.state;
  //   currentPage++;
  //   if (currentPage > maxPage) {
  //     return false;
  //   }
  //   this.setState({
  //     currentPage,
  //   });
  //   this.getData(currentPage);
  // };

  nextPage = (maxPage) => {
    let { currentPage } = this.state;
    currentPage++;
    if (currentPage > maxPage) {
      return false;
    }
    this.setState({
      currentPage,
    });
    this.getData(currentPage);
  };

  //
  // prePage = () => {
  //   let { currentPage } = this.state;
  //   currentPage--;
  //   if (currentPage < 0) {
  //     return false;
  //   }
  //   this.setState({ currentPage });
  //   this.getData(currentPage);
  // };

  prePage = () => {
    let { currentPage } = this.state;
    currentPage--;
    if (currentPage < 0) {
      return false;
    }
    this.setState({ currentPage });
    this.getData(currentPage);
  };

  renderPage = (maxPage) => {
    // const { currentPage } = this.state;

    let startPage = 0;
    let endPage = 0;
    let start = 0;
    let end = 0;

    // if (currentPage + 2 >= maxPage) {
    //   end = maxPage;
    // } else {
    //   end = currentPage + 2;
    // }
    // if (currentPage - 2 <= 1) {
    //   start = 1;
    // } else {
    //   start = currentPage - 2;
    // }

    // startPage = start;
    // endPage = end;
    // const extraStart = start - (currentPage - 2);
    // if (extraStart > 0) {
    //   end = end + extraStart;
    // }
    // const extraEnd = currentPage + 2 - endPage;
    // if (extraEnd > 0) {
    //   start = start - extraEnd;
    // }
    // if (start <= 1) {
    //   start = 1;
    // }
    // if (end >= maxPage) {
    //   end = maxPage;
    // }
    const { currentPage } = this.state;
    if (currentPage + 2 >= maxPage) {
      end = maxPage;
    } else {
      end = maxPage - 2;
    }

    console.log('startPage', startPage);
    console.log('endPage', endPage);

    let html = [];

    for (let i = start; i <= end; i++) {
      html.push(
        <button
          key={i}
          className={`${currentPage === i ? 'active' : ''}`}
          onClick={() => this.gotoPage(i)}
        >
          {i}
        </button>
      );
    }

    return html;
  };

  render() {
    const { listData, total, limit, currentPage } = this.state;
    console.log('total', total);
    console.log('limit', limit);
    const extraPage = total % limit > 0 ? 1 : 0;
    const maxPage = Math.floor(total / limit) + extraPage;
    console.log('maxPage', maxPage);

    return (
      <div>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              <h1>
                {post.id}-{post.email}
              </h1>
            </div>
          );
        })}

        <button
          onClick={() => this.prePage()}
          disabled={currentPage === 1 ? 'disabled' : ''}
        >
          pre
        </button>
        {this.renderPage(maxPage)}

        <button
          onClick={() => this.nextPage(maxPage)}
          disabled={currentPage === maxPage ? 'disabled' : ''}
        >
          next
        </button>
      </div>
    );
  }
}
export default Users;
