  handleRegister = (e) => {
    e.preventDefault();
    const { email, password } = this.state;
    const isValid = this.validateForm();












    
    if (!isValid) {
      return;
    }
    axios
      .post('http://localhost/php_api/users/register.php', {
        email: email,
        password: password,
      })
      .then((res) => {
        console.log(res.data);
        this.props.history.push('/');
        // return <Redirect to="/home" />;
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    const { email, errors, password, confirmPassword } = this.state;
    return (
      <div id='login'>
        <h1 className='text-center text-black heading pt-5'>Facebook</h1>
        <div className='container'>
          <div
            id='login-row'
            className='row justify-content-center align-items-center'
          >
            <div id='login-column' className='col-md-6'>
              <div id='login-box' className='col-md-12'>
                <form id='login-form' className='form' action='' method='post'>
                  <h3 className='text-center text-info text-white'>
                    Register account
                  </h3>
                  <div className='form-group'>
                    <div>
                      <input
                        type='email'
                        value={email}
                        className='form-control'
                        placeholder='Email'
                        onChange={(e) => this.handChangeEmail(e)}
                      />
                      <p>{errors.email} </p>
                    </div>
                  </div>
                  <br />
                  <div className='form-group password-box'>
                    <input
                      type='password'
                      value={password}
                      className='form-control'
                      placeholder='Password'
                      autoComplete='on'
                      onChange={(e) => this.handleChangePassword(e)}
                    />
                    {errors.password}
                  </div>
                  <br />
                  <div className='form-group password-box'>
                    <input
                      type='password'
                      value={confirmPassword}
                      className='form-control'
                      placeholder='Confirm password'
                      autoComplete='on'
                      onChange={(e) => this.handleConfirmPassword(e)}
                    />
                    {errors.confirmPassword}
                  </div>

                  <div className='form-group'>
                    <br />
                    <button
                      type='button'
                      className='btn btn-info btn-md '
                      onClick={(e) => this.handleRegister(e)}
                    >
                      <h1>Register</h1>
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
