import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      data: [],
      totalCount: 0,
      page: 1,
    };
  }

  componentDidMount() {
    this.getData(tr);
    this.setState({
      data: [],
    });
    console.log('hahah', this.setState);
  }



  getData() {
    const { page } = this.state;
    axios
      .get(`http://localhost/php_api/users/pagination.php/?page=${page}`)
      .then((json) => {
        console.log('check', json);
        this.setState({
          data: json,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }


  render() {
    const theData = this.state.data.map((post) => {
      return (
        <div className='content'>
          <p>{post.email}</p>
        </div>
      );
    });

    return (
      <div>
        {theData}
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
          {/* {data.map((post, i) => {
            return (
              <div className='content' key={i}>
                {post.email}
                <br />
              </div>
            );
          })} */}
        </table>
      </div>
    );
  }
}
export default Pagination;
