import React from 'react';
class Counter extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],

      job: '',
      jobs: [],
    };
  }
  handleDelete = (e, id) => {
    e.preventDefault();
    const post = this.state.post.filter((post) => post.id !== id);
    console.log('check', id);
    this.setState({
      post: post,
    });
  };

  handleChange = (e) => {
    this.setState({
      job: e.target.value,
    });
  };
handleSubmit=()=>{
  this.setState({
    ...jobs
  })
}
  render() {
    const { post, job } = this.state;
    return (
      <div>
        {post.map((post) => (
          <div className='content' key={post.id}>
            <h1>{post.id}</h1>
            <h1>{post.value}</h1>

            <button
              className='btn btn-danger'
              onClick={(e) => this.handleDelete(e, post.id)}
            >
              Delete
            </button>
          </div>
        ))}

        <div>
          <input value={job} onChange={(e) => this.handleChange(e)} />
          <button onClick={() => this.handleSubmit()}>Add</button>
          <ul>
            {jobs.map((job, id) => {
              <li key={id}>{job}</li>;
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default Counter;
