import React from 'react';
import axios from 'axios';
class Pagination extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      limit: 5,
      posts: [],
    };
  }

  async componentDidMount() {
    let res = await axios.get('http://localhost/php_api/users/pagination.php');
    console.log('check', this.setState);
    this.setState({
      posts: res.data.data,
    });
  }

  render() {
    const { posts, limit, currentPage } = this.state;
    const indexOfLastPost = currentPage * limit;
    const indexOfFirstPost = indexOfLastPost - limit;
    const currentPosts = this.state.posts.slice(
      indexOfFirstPost,
      indexOfLastPost
    );

    //Implement page numbers
    const pageNumbers = [];

    for (
      let i = 1;
      i <= Math.ceil(this.state.posts.length / this.state.postsPerPage);
      i++
    ) {
      pageNumbers.push(i);
    }

    //Set current page
    const setPage = (pageNum) => {
      this.setState({ currentPage: pageNum });
    };
    return (
      <div>
        <table className='table table-bordered'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Content</th>
              <th>Author</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <tr key={post.id}>
                <td>{post.email}</td>
                <td>{post.pass}</td>
              </tr>
            ))}
          </tbody>
          <div className='w-full flex justify-around'>
            {pageNumbers.map((pageNum, index) => (
              <span
                key={index}
                className={
                  pageNum === this.state.currentPage
                    ? 'cursor-pointer flex items-center justify-center w-12 h-12 border-2 rounded-full bg-blue-500 text-white'
                    : 'cursor-pointer flex items-center justify-center w-12 h-12 border-2 rounded-full'
                }
                onClick={() => {
                  setPage(pageNum);
                }}
              >
                {pageNum}
              </span>
            ))}
          </div>
        </table>
      </div>
    );
  }
}
export default Pagination;
