import React from 'react';
import axios from 'axios';
import './search.css';
import Pagination from './Pagination';

// const queryString = require('query-string');

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 1,
      listData: [],
      metaInfo: {
        hasMoreItems: false,
        cursor: '',
        meta: {},
      },
      totalCount: 0,
    };
  }

  componentDidMount = () => {
    this.loadData();
    // const parsed = queryString.parse(this.props.location.search);
    // const keyword = parsed.query || '';
    // if (!keyword) {
    //   this.props.history.push('/');
    // }
    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }
  };

  loadData = async () => {
    const { metaInfo, limit } = this.state;

    const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

    let result = await axios({
      method: 'post',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: ${limit}
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    console.log('checked', result);
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    // const { totalCount, perPage } = this.state;
    this.setState({
      listData: !metaInfo.cursor ? data.concat(data) : data,
      metaInfo: meta,
      totalCount: meta.totalCount || 0,
    });
  };

  loadMore = () => {
    this.loadData();
  };

  render() {
    const { listData, totalCount, limit } = this.state;
    const perPage = Math.ceil(totalCount / limit);

    return (
      <div className='container'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
              <br />
              <img src={post.thumbnail} style={{ maxWidth: 200 }} />
            </div>
          );
        })}
        <br></br>

        <Pagination
          postsPerPage={this.perPage()}
          totalPosts={listData.length}
          paginate={this.state.paginate}
          onClick={this.loadMore}
        />
      </div>
    );
  }
}

export default Search;
