      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],

      job: '',
      jobs: [],
    };
  }
  handleDelete = (e, id) => {
    e.preventDefault();
    const post = this.state.post.filter((post) => post.id !== id);
    console.log('check', id);
    this.setState({
      post: post,
    });
  };
  render() {
    const { post, c } = this.state;
    return (
      <div>
        {post.map((post) => (
          <div className='content' key={post.id}>
            <h1>{post.id}</h1>
            <h1>{post.value}</h1>

            <button
              className='btn btn-danger'
              onClick={(e) => this.handleDelete(e, post.id)}
            >
              Delete
            </button>
          </div>
        ))}



