import React from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
const queryString = require('query-string');

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      listData: [],
      metaInfo: {
        hasMoreItems: false,
        cursor: '',
      },
      totalCount: 20,
      perPage: 2,
    };
  }

  componentDidMount = () => {
    this.loadData();
    const parsed = queryString.parse(this.props.location.search);
    const keyword = parsed.query || '';
    if (!keyword) {
      this.props.history.push('/');
    }
    const token = localStorage.getItem('token') || '';
    console.log('check', token);

    if (!token) {
      this.props.history.push('/login');
    }
  };

  loadData = async () => {
    const { metaInfo } = this.state;

    const url = `https://d2irr0mp54galb.cloudfront.net/cms/read/en-AU`;

    let result = await axios({
      method: 'post',
      url,
      data: {
        operationName: 'ListBlogPosts',
        query: `query ListBlogPosts {
            listBlogPosts(
              limit: 2
              sort: postDate_DESC
              after: "${metaInfo.cursor || ''}="
            ) {
              data {
                content
                slug
                title
                thumbnail
              }
              meta {
                totalCount
                hasMoreItems
                cursor
              }
             }
            }`,
      },
      headers: {
        Authorization: `a67550392f105ba5ecb8765d866b06a2735f96269b96767c`,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    const {
      data: {
        listBlogPosts: { data, meta },
      },
    } = result;

    const { listData, totalCount, perPage } = this.state;
    this.setState({
      listData: !metaInfo.cursor ? data : [ ...listData],
      metaInfo: meta,
      pageCount: Math.ceil(totalCount / perPage),
    });
  };

  loadMore = () => {
    this.loadData();
  };

  render() {
    const { listData, metaInfo } = this.state;

    return (
      <div className='container'>
        {listData.map((post, i) => {
          return (
            <div className='content' key={i}>
              {post.title}
              <br />
              <img src={post.thumbnail} style={{ maxWidth: 200 }} />
            </div>
          );
        })}
        <br></br>

        <ReactPaginate
          previousLabel={'prev'}
          nextLabel={'next'}
          breakLabel={'...'}
          breakClassName={'break-me'}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={() => this.loadMore()}
          containerClassName={'pagination'}
          subContainerClassName={'pages pagination'}
          activeClassName={'active'}
        />
      </div>
    );
  }
}

export default Search;
