import React from 'react';
class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      post: [
        { id: 1, value: 4 },
        { id: 2, value: 5 },
        { id: 3, value: 6 },
      ],
      //
      job: '',
      jobs: [],
      //
      toggle: true,
      //
      random: ['Love', 'No Love'],
    };
  }
  handleDelete = (e, id) => {
    e.preventDefault();

    const post = this.state.post.filter((post) => post.id !== id);
    console.log('check', id);
    this.setState({
      post: post,
    });
  };

  handleChange = (e) => {
    this.setState({
      job: e.target.value,
    });
  };
  handleAdd = () => {
    if (this.state.job === '') {
      return;
    }
    this.state.jobs.push(this.state.job);
    this.setState({
      job: '',
    });
  };

  Delete = (id) => {
    const deleteTask = [...this.state.jobs];
    deleteTask.splice(id, 1);
    console.log('checkID', id);
    this.setState({
      jobs: deleteTask,
    });
  };
  ///
  handShowHide = () => {
    this.setState({
      toggle: !this.state.toggle,
    });
  };
  ///
  handleLove = (random) => {
    const index = this.state.random;
    index.Math.floor(Math.random() * random.length);
    console.log('length', index);
    this.setState({
      random: index,
    });
  };

  render() {
    const { post, job, jobs } = this.state;
    return (
      <div>
        {post.map((post, id) => (
          <div className='content' key={id}>
            <h1>{post.id}</h1>
            <h1>{post.value}</h1>

            <button
              className='btn btn-danger'
              onClick={(e) => this.handleDelete(e, post.id)}
            >
              Delete
            </button>
          </div>
        ))}

        <div>
          <input
            value={job}
            id={job.id}
            onChange={(e) => this.handleChange(e)}
          />
          <button onClick={() => this.handleAdd()}>Add</button>
          {jobs.map((job, id) => (
            <div className='content' key={id}>
              <h1>{job}</h1>

              <button
                className='btn btn-danger'
                onClick={() => this.Delete(id)}
              >
                Delete
              </button>
            </div>
          ))}
          <br />
        </div>
        <button onClick={() => this.handShowHide()}>Detail</button>

        {this.state.toggle ? <h1>Kim Dung Lucky Ng√°o</h1> : null}
        <br />
        <br />
        <button onClick={() => this.handleRandom(this.state.random)}>Chose</button>
      </div>
    );
  }
}

export default Counter;
