import React from 'react';
import axios from 'axios';
import './Users.css';

class Users extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      limit: 5,
      listData: [],
      total: 0,
      currentPage: 1,
    };
  }
  //
  componentDidMount() {
    const token = localStorage.getItem('token') || '';
    console.log('check', token);
    if (!token) {
      this.props.history.push('/login');
    }

    this.getData(true);
  }

  getData = async (firstLoad) => {
    const { currentPage, limit } = this.state;
    const url = `http://localhost/php_api/users/list.php/?page=${currentPage}&limit=${limit}`;

    let result = await axios({
      method: 'get',
      url,
      data: {
        email: '',
      },
      headers: {
        Authorization: ``,
        'Content-Type': 'application/json',
      },
    });

    result = result.data;
    const { data } = result;

    console.log('checked', result);
    const { listData } = this.state;

    this.setState({
      listData: firstLoad ? data : listData.concat(data),
      total: result.total,
    });
