import React from 'react';
import { Link } from 'react-router-dom';

import './Header.css';
class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isShowSearch: false,
      searchData: '',
      searchResults: [],
    };
  }

  showSearch = () => {
    this.setState({ isShowSearch: !this.state.isShowSearch });
  };

  handleSearchChange = (e) => {
    this.setState({
      searchData: e.target.value,
    });
  };

  handleSubmitSearch(e, props) {
    e.preventDefault();
    if (!this.state.searchData) {
      return false;
    }
    props.history.push(
      `/search?query=${encodeURIComponent(this.state.searchData)}`
    );
  }

  render() {
    const { isShowSearch, searchData } = this.state;

    return (
      <div className='container'>
        <div className='header'>
          <div className='header-top'>
            <div className='top-header'>
              <Link to='/'>
                <img src='/picture/travel-homepage.png' alt='' />
              </Link>
            </div>
          </div>
          <div className='main-header'>
            <nav
              class='navbar navbar-expand-lg navbar-light'
              style={{ height: 50, paddingBottom: 30 }}
            >
              <div class='form-search'>
                <div className='toggle-icon' onClick={this.showSearch}>
                  {isShowSearch ? (
                    <i class='fa-solid fa-xmark' />
                  ) : (
                    <i class='fa-solid fa-magnifying-glass' />
                  )}
                </div>

                <form className='search-box'>
                  {isShowSearch && (
                    <input
                      type='text'
                      className='text search-input'
                      value={searchData}
                      onChange={this.handleSearchChange}
                      placeholder='Search'
                    />
                  )}
                  {isShowSearch && (
                    <input
                      type='button'
                      value='Search'
                      className='btn-submit'
                      onClick={(e) =>
                        this.handleSubmitSearch(e, this.props.children.props)
                      }
                    />
                  )}
                </form>
              </div>
              <div class='dropdown'>
                <button
                  class='btn btn-primary dropdown-toggle'
                  type='button'
                  id='dropdownMenuButton'
                  data-toggle='dropdown'
                  aria-haspopup='true'
                  aria-expanded='false'
                >
                  Dropdown button
                </button>
                







              </div>
            </nav>
          </div>

          {/* <section class='banner'>
            <img src='/picture/main-picture.jpg' alt='banner' />
          </section> */}

          <div
            id='carouselExampleControls'
            class='carousel slide'
            data-ride='carousel'
          >
            <div class='carousel-inner'>
              <div class='carousel-item active'>
                <img
                  class='d-block w-100'
                  src='/picture/main-picture.jpg'
                  alt='First slide'
                />
              </div>
              <div class='carousel-item'>
                <img
                  class='d-block w-100'
                  src='/picture/main-picture.jpg'
                  alt='Second slide'
                />
              </div>
              <div class='carousel-item'>
                <img
                  class='d-block w-100'
                  src='/picture/main-picture.jpg'
                  alt='Third slide'
                />
              </div>
            </div>
            <a
              class='carousel-control-prev'
              href='#carouselExampleControls'
              role='button'
              data-slide='prev'
            >
              <span
                class='carousel-control-prev-icon'
                aria-hidden='true'
              ></span>
              <span class='sr-only'>Previous</span>
            </a>
            <a
              class='carousel-control-next'
              href='#carouselExampleControls'
              role='button'
              data-slide='next'
            >
              <span
                class='carousel-control-next-icon'
                aria-hidden='true'
              ></span>
              <span class='sr-only'>Next</span>
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
